import React from "react";
import { useState } from "react";
import "./App.css";

function NavBar() {
  return (
    <nav>
      <ul>
        <li>Home</li>
        <li>Products</li>
        <li>About</li>
        <li>Contact</li>
      </ul>
    </nav>
  );
}

function ToDo() {
  const [newToDo, setNewToDo] = useState("");
  const [toDos, setToDos] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();

    setToDos((currentToDos) => {
      return [
        ...currentToDos,
        { id: crypto.randomUUID(), title: newToDo, completed: false },
      ];
    });
  }
  return (
    <>
    <div className="toDo">
      <form onSubmit={handleSubmit} className="toDoForm">
        <div>
          <label htmlFor="new-todo"><h4>Add New To Do</h4></label>
          <input
            id="new-todo"
            value={newToDo}
            onChange={e => setNewToDo(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-success">Add ToDo</button>
      </form>
      <ul className="list">
      <h4>My To Do List</h4>
      {toDos.map(toDo)}
        <li className="toDoItem">
        <label>
          <input type="checkbox" />
        </label>
        <button className="btn btn-danger">Delete</button>
        </li>
        <li className="toDoItem">
        <label>
          <input type="checkbox" />
        </label>
        <button className="btn btn-danger">Delete</button>
        </li>
      </ul>
    </div>
    </>
  )
}

function Profile(props) {
  console.log(props);
  return (
    <div>
      <h2>{props.name}</h2>
      <p>{props.info}</p>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <NavBar />
      <ToDo />
      <Profile name="Ludvig" info="people pleaser"/>
      <Profile name="Sigrid" info="manipulator"/>
    </div>
  );
}

export default App;
