import React from "react";
import { useState } from "react";
import "./App.css";

function NavBar() {
  return (
    <nav>
      <ul>
        <li>Home</li>
        <li>Products</li>
        <li>About</li>
        <li>Contact</li>
      </ul>
    </nav>
  );
}

function ToDo() {
  const [newToDo, setNewToDo] = useState("");
  const [toDos, setToDos] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();

    setToDos((currentToDos) => {
      return [
        ...currentToDos,
        { id: crypto.randomUUID(), title: newToDo, completed: false },
      ];
    });
    setNewToDo("");
  }
  function checkedToDo(id, checked) {
    setToDos(currentToDos => {
      return currentToDos.map(toDo => {
        if (toDo.id === id) {
          return { ...toDo, completed: checked};
        }
        return toDo;
      });
    });
  }

  return (
    <>
    <div className="toDo">
      <form onSubmit={handleSubmit} className="toDoForm">
        <div>
          <label htmlFor="new-todo"><h4>Add New To Do</h4></label>
          <input
            id="new-todo"
            value={newToDo}
            onChange={e => setNewToDo(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-success">Add ToDo</button>
      </form>
      <h4>My To Do List</h4>
      <ul className="list">
      {toDos.map(toDo => {
        return (
        <li key={toDo.id} className="toDoItem">
        <label>
          <input type="checkbox" checked={toDo.completed} onChange={e => checkedToDo(toDo.id, e.target.checked)}/>
          {toDo.title}
        </label>
        <button className="btn btn-danger">Delete</button>
        </li>
        )
      })}
      </ul>
    </div>
    </>
  )
}

function Profile(props) {
  console.log(props);
  return (
    <div>
      <h2>{props.name}</h2>
      <p>{props.info}</p>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <NavBar />
      <ToDo />
      <Profile name="Ludvig" info="people pleaser"/>
      <Profile name="Sigrid" info="manipulator"/>
    </div>
  );
}

export default App;
