import React from 'react';
import { Card } from 'react-bootstrap';

function Product({ product }) {
    const { title, description, price, discountedPrice, image } = product;
    const quantity = 0;

    return (
        <Card className='product-card' style={{ width: '18rem', margin: '10px' }}>
            <Card.Img id='product-img' variant="top" src={image.url} alt={image.alt || title} />
            <Card.Body>
                <Card.Title>{title}</Card.Title>
                <Card.Text>{description}</Card.Text>
                <Card.Text>Regular Price: ${price.toFixed(2)}</Card.Text>
                {discountedPrice < price && (
                    <Card.Text>Discounted Price: ${discountedPrice.toFixed(2)}</Card.Text>
                )}
                <div className='mt-auto'>
                    {quantity === 0 ? (
                        <button className='btn btn-primary w-100'>+ Add To Cart</button>
                    ) : (
                        <div className='d-flex align-items-center flex-column' style={{ gap: '0.5rem' }}>
                            <div className='d-flex align-items-center justify-content-center' style={{ gap: '0.5rem' }}>
                                <button className='btn btn-primary'>-</button>
                                <div>
                                    <span className='fs-3'>{quantity}</span> <br />Added
                                </div>
                                <button className='btn btn-primary'>+</button>
                            </div>
                            <button className='btn btn-danger w-100'>Remove</button>
                        </div>
                    )}
                </div>
            </Card.Body>
        </Card>
    );
}

export default Product;
