import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useShoppingCart } from '../context/ShoppingCartContext';
import { Dropdown, DropdownButton } from 'react-bootstrap';

function ShoppingCart() {
    const [showDropdown, setShowDropdown] = useState(false);
    const { cartItems, getItemQuantity, increaseCartQuantity, decreaseCartQuantity, removeItemFromCart } = useShoppingCart();

    const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="shopping-cart">
            <DropdownButton
                id="dropdown-basic-button"
                title="Cart"
                onMouseEnter={() => setShowDropdown(true)}
                onMouseLeave={() => setShowDropdown(false)}
                show={showDropdown}
            >
                <Dropdown.Menu>
                    {cartItems.map(item => (
                        <Dropdown.Item key={item.id}>
                            <div className="cart-item">
                                <img src={item.image.url} alt={item.image.alt} style={{ width: '50px', height: '50px' }} />
                                <div>
                                    <p>{item.title}</p>
                                    <p>{item.quantity} x ${item.price.toFixed(2)}</p>
                                </div>
                                <div>
                                    <button onClick={() => increaseCartQuantity(item.id)}>+</button>
                                    <button onClick={() => decreaseCartQuantity(item.id)}>-</button>
                                    <button onClick={() => removeItemFromCart(item.id)}>Remove</button>
                                </div>
                            </div>
                        </Dropdown.Item>
                    ))}
                    <Dropdown.Divider />
                    <Dropdown.Item>
                        <div>Total: ${totalPrice.toFixed(2)}</div>
                    </Dropdown.Item>
                    <Dropdown.Item as={Link} to="/checkout">Go to Checkout</Dropdown.Item>
                </Dropdown.Menu>
            </DropdownButton>
        </div>
    );
}

export default ShoppingCart;
