import React, { useState, useEffect } from 'react';
import { Route, Routes } from "react-router-dom";
import "./assets/styles/App.css";
import { Navbar } from "./layout/Navbar";
import Products from "./pages/Products";
import Contact from "./pages/Contact";
import Home from "./pages/Home";



function App() {
  const [products, setProducts] = useState(null);

  // Display a loading message or spinner when data is being fetched
  if (!products) {
      return <div>Loading...</div>;
  }


  useEffect(() => {
    fetch('https://v2.api.noroff.dev/online-shop')
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            if (Array.isArray(data)) { // Check if the response is an array
                setProducts(data);
            } else {
                throw new Error('Data is not an array');
            }
        })
        .catch(error => {
            console.error('Error fetching data: ', error);
            setProducts([]); // Set products to an empty array in case of error
        });
}, []);


  return (
      <>
          <Navbar />
          <div className='container'>
              <Routes>
              <Route path="/" element={<Home products={products ? products.slice(-5) : []} />} />

                  <Route path="/products" element={<Products products={products} />} />
                  <Route path="/contact" element={<Contact />} />
              </Routes>
          </div>
      </>
  );
}

export default App;
